CREATE FUNCTION [dbo].[QuestionMultipleTags] 
(
	@QuestionGUID NVARCHAR(MAX)
)
RETURNS NVARCHAR(MAX)
AS

BEGIN
DELETE FROM Questions WHERE QuestionGUID = @QuestionGUID
END;

BEGIN
INSERT INTO [Questions] Values (@QuestionGUID, @Text, @QuestionType, @CategoryGUID,@LevelGUID)
END;

